script:
frappe.ui.form.on('Item', {
    item_group: function(frm) {
        if (frm.doc.item_group === "Services") {
            frm.set_value('is_stock_item', 0);
            frm.set_value('brand', 'Services');
        } else {
            frm.set_value('is_stock_item', 1);
            frm.set_value('brand', null);
        }
    }
});

frappe.ui.form.on('Supplier', {
    setup: function(frm) {
        frm.set_query('supplier_group', function() {
            return {
                filters: {
                    is_group: 0
                }
            };
        });
    }
});

frappe.ui.form.on('Purchase Invoice', {
    posting_date: function(frm) {
        if (frm.doc.posting_date) {
            frm.set_value('due_date', frm.doc.posting_date);
        }
    }
});
frappe.ui.form.on('Purchase Invoice', {
    validate: function(frm) {
        if (frm.doc.posting_date) {
            frm.set_value('due_date', frm.doc.posting_date);
        }
    }
});

frappe.ui.form.on('Purchase Invoice', {
    posting_date: function(frm) {
        if (frm.doc.posting_date && frm.doc.bill_no) {
            frm.set_value('bill_date', frm.doc.posting_date);
        }
    }
});
frappe.ui.form.on('Purchase Invoice', {
    bill_no: function(frm) {
        if (frm.doc.posting_date && frm.doc.bill_no) {
            frm.set_value('bill_date', frm.doc.posting_date);
        }
    }
});
