root_element.addEventListener("click", async function(e) {
    const newdoc = frappe.model.get_new_doc('Sales Invoice');

    await frappe.run_serially([
        () => frappe.model.with_doctype('Sales Invoice'),
        () => frappe.model.set_default_values(newdoc)
    ]);

    newdoc.is_return = 1;
    newdoc.update_stock = 1;
    newdoc.posting_time = '00:00:02';
    newdoc.set_posting_time = 1;
    newdoc.update_billed_amount_in_sales_order = 0;
    newdoc.update_billed_amount_in_delivery_note = 0;

    frappe.model.sync(newdoc);
    frappe.set_route('Form', newdoc.doctype, newdoc.name);
});

root_element.addEventListener("click", async function(e) {
    // Get default company
    const default_company = frappe.defaults.get_default("Company");

    // Fetch the round_off_account from the Company doctype
    const company = await frappe.db.get_doc("Company", default_company);

    // Create new Stock Entry
    const newdoc = frappe.model.get_new_doc("Stock Entry");

    await frappe.run_serially([
        () => frappe.model.with_doctype("Stock Entry"),
        () => frappe.model.set_default_values(newdoc)
    ]);

    // Set desired field values
    newdoc.stock_entry_type = "Material Transfer";
    newdoc.posting_time = '00:00:05';
    newdoc.expense_account = company.round_off_account;
    newdoc.to_warehouse = null;

    // Sync and route to new form
    frappe.model.sync(newdoc);
    frappe.set_route("Form", newdoc.doctype, newdoc.name);
});

